mapper.select:
SELECT p_id id,n_user.u_username username,n_doctor.d_name dname, p_date times,p_state state,p_version version,n_patientMedicine.p_price price FROM n_patientMedicine INNER JOIN n_user ON n_patientMedicine.p_userId = n_user.u_id INNER JOIN n_doctor ON n_patientMedicine.p_doctorId=n_doctor.d_id WHERE 1=1
AND username LIKE CONCAT('%',#{username},'%')
AND dname LIKE CONCAT('%',#{doctorName},'%')
AND p_id=#{id}
AND p_state=#{state}
AND p_delete=0 ORDER BY n_patientMedicine.p_id

SELECT DATE_FORMAT(p_date, '%m%d') days, COUNT(p_id) COUNT
FROM n_patientMedicine
GROUP BY days;
